<template>
  <div>
    <img @click="cambiarIdioma" :src="rutaBandera" alt="Bandera idioma">
  </div>
</template>

<script>
export default {
  name: 'CambiarIdioma',
  setup () {
    const banderaChile = 'data:image/png;base64,'
    const banderaUK = 'data:image/png;base64,'
    return {
      banderaChile,
      banderaUK
    }
  },
  data () {
    return {
      rutaBandera: this.banderaUK,
      idiomaSeleccionado: this.$idiomaGlobal
    }
  },
  methods: {
    cambiarIdioma () {
      const toggleLang = (this.idiomaSeleccionado === 'english') ? 'spanish' : 'english'
      this.$root.$idiomaGlobal = toggleLang
      this.idiomaSeleccionado = toggleLang
      this.onClickButton()
    },
    onClickButton () {
      this.$emit('clicked', this.idiomaSeleccionado)
      if (this.rutaBandera === this.banderaChile) {
        this.rutaBandera = this.banderaUK
      } else {
        this.rutaBandera = this.banderaChile
      }
    }
  },
  mounted () {
    this.idiomaSeleccionado = this.$root.$idiomaGlobal
  }
}
</script>

<style>
button {
  padding: 1em;
}
</style>
